import React, { Component } from 'react';
import { getList, addToList, deleteItem, updateItem } from './UserFunctions';
import jwt_decode from 'jwt-decode'
import moment from 'moment';
import SignaturePad from 'react-signature-canvas';

import Grow from '@material-ui/core/Fade';
import withStyles from '@material-ui/core/styles/withStyles';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import Dialog from '@material-ui/core/Dialog';
import AddCircleIcon from '@material-ui/icons/AddCircle';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import IconButton from '@material-ui/core/IconButton';
import CloseIcon from '@material-ui/icons/Close';
import Slide from '@material-ui/core/Slide';
import TextField from '@material-ui/core/TextField';
import Grid from '@material-ui/core/Grid';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CircularProgress from '@material-ui/core/CircularProgress';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';

const styles = (theme) => ({
	content: {
		flexGrow: 1,
		padding: theme.spacing(3)
	},
	appBar: {
		position: 'relative'
	},
	title: {
		marginLeft: theme.spacing(2),
		flex: 1
	},
	submitButton: {
		display: 'block',
		color: 'white',
		textAlign: 'center',
		position: 'absolute',
		top: 14,
		right: 10
	},
	floatingButton: {
		position: 'fixed',
		bottom: 0,
		right: 0
	},
	form: {
		width: '98%',
		marginLeft: 13,
		marginTop: theme.spacing(3)
	},
	toolbar: theme.mixins.toolbar,
	root: {
		minWidth: 470
	},
	bullet: {
		display: 'inline-block',
		margin: '0 2px',
		transform: 'scale(0.8)'
	},
	pos: {
		marginBottom: 12
	},
	uiProgess: {
		position: 'fixed',
		zIndex: '1000',
		height: '31px',
		width: '31px',
		left: '50%',
		top: '35%'
	},
	dialogeStyle: {
		maxWidth: '50%'
	},
	viewRoot: {
		margin: 0,
		padding: theme.spacing(2)
	},
	closeButton: {
		position: 'absolute',
		right: theme.spacing(1),
		top: theme.spacing(1),
		color: theme.palette.grey[500]
    },
    sigContainer: {
        width: '100%',
        height: '40vh',
        margin: '0 auto',
        backgroundColor: '#fff',
        border: '1px solid black'
    },
});

const Transition = React.forwardRef(function Transition(props, ref) {
	return <Slide direction="up" ref={ref} {...props} />;
});

class Todo extends Component {
    constructor() {
        super()
        this.state = {
            id: '',
            title: '',
            reminder: '',
            userid: null,
            open: '',
            buttonType: '',
            items: [],
            errors: [],
            uiLoading: true,
            canvasDataURL: null,
            showSigPad: false
        }

        this.sigPad = React.createRef();
    }

    componentDidMount () {
        const token = localStorage.usertoken;
        const decoded = jwt_decode(token);
        getList(decoded.identity.id).then(data => {
            this.setState({
                title: '',
                reminder: '',
                items: [...data]
            });
        })
        this.setState({userid: decoded.identity.id, uiLoading: false});
    }

    onChange = e => {
        this.setState({
            [e.target.name] : e.target.value,
        })
    }

    getAll = () => {
        this.setState({uiLoading: true});
        getList(this.state.userid).then(data => {
            console.log(data);
            this.setState({
                id: '',
                title: '',
                reminder: '',
                open: '',
                buttonType: '',
                items: [...data],
                uiLoading: false,
                canvasDataURL: null,
                showSigPad: false
            });
        })
    }

    onSubmit = e => {
        e.preventDefault();
        this.setState({ editDisabled: '' })
        addToList(this.state.userid ,this.state.title, this.state.reminder, this.state.canvasDataURL).then(() => {
            this.getAll()
        })
        this.handleClose();
    }

    onUpdate = e => {
        console.log('update called');
